import { useContext, useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import {
  Typography, Tabs, Image, message,
} from 'antd';
import UserInfo from '../../components/EmployeeInfo';
import Img from '../../components/common/Img';
// import authContext from '../../context/authContext';

const { Title } = Typography;
const { TabPane } = Tabs;

const EmployeeProfile = () => {
  const [employeeData, setEmployeeData] = useState({});

  // const { isAuth } = useContext(authContext);
  const { employeeId } = useParams();

  useEffect(() => {
    const myAbortController = new AbortController();
    async function fetchingEmployeeData(id) {
      try {
        const data = await axios.get(`/employee/${id}`, { signal: myAbortController.signal });
        setEmployeeData(data);
      } catch (err) {
        message.error(err.response.data.Error);
      }
    }
    fetchingEmployeeData(employeeId);
    return () => {
      myAbortController.abort();
    };
  }, []);

  return (
    <>
      { employeeData.profileImage ? (
        <>
          <Image placeholder height="250px" width="100%" alt="cover image" src={employeeData.coverImage} fallback="data:image/png;base64," />
          <div className="flex mx-20 text-gray-400 justify-between">
            <div className="profile -mt-16">
              <Img className="profile-img" alt="profile image" src={employeeData.profileImage} />
              <Title level={5}>{`${employeeData.firstName} ${employeeData.lastName}`}</Title>
              <span className="ml-4">
                {employeeData.status}
              </span>
            </div>
          </div>
          <Tabs tabPosition="left" className="profile-tabs">
            <TabPane tab="Information" key="1">
              <UserInfo isAuth={isAuth} data={employeeData} />
            </TabPane>
            <TabPane tab="Saved" key="2" />
          </Tabs>
        </>
      ) : null}
    </>
  );
};

export default EmployeeProfile;
